import './App.css';


const cardImages = [
  {"src": "/images/img1.jpg"},
  {"src": "/images/img2.jpg"},
  {"src": "/images/img3.jpg"},
  {"src": "/images/img4.jpeg"},
  {"src": "/images/img5.jpg"},
  {"src": "/images/img6.jpg"}
]


function App() {

  // shuffle the cards 
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({...card, id: Math.random()}))
  }
  return (
    <div className="app">
     <h1>Memory Game</h1>
      <button>New Game</button>

    </div>
  );
}

export default App;
