import React from 'react'


function Card({item, id, handleClick }) {
  const itemClass = item.stat ? "active" + item.stat : ""

  return (
    <div  onClick={() => handleClick(id)}>
        < img className={"card" + itemClass} src={item.img} />

    </div>
  )
}

export default Card
