import { useEffect, useState } from 'react';
import './App.css';
import Login from './components/Login';
import SignUp from './components/SignUp';
import SingleCard from './components/SingleCard.js'
import { StreamChat} from "stream-chat";
import {Chat} from 'stream-chat-react';
import Cookies from "universal-cookie";
import JoinGame from './components/JoinGame';


const cardImages = [
  {"src": "/images/img1.jpg", matched: false},
  {"src": "/images/img2.jpg", matched: false},
  {"src": "/images/img3.jpg", matched: false},
  {"src": "/images/img4.jpeg", matched: false},
  {"src": "/images/img5.jpg", matched: false},
  {"src": "/images/img6.jpg", matched: false}
]


function App() {

  console.log(process.env.REACT_APP_MY_API_KEY)
  const api_key = process.env.REACT_APP_MY_API_KEY
  const cookies = new Cookies();
  const token = cookies.get("token");
  const client = StreamChat.getInstance(api_key);
  const [isAuth, setIsAuth] = useState(false);

  const logOut = () => {
    cookies.remove("token");
    cookies.remove("userId");
    cookies.remove("firstName");
    cookies.remove("lastName");
    cookies.remove("hashedPassword");
    cookies.remove("channelName");
    cookies.remove("username");
    client.disconnectUser();
    setIsAuth(false)
  };

  // if (token) {
  //   client.connectUser({
  //     id: cookies.get("userId"),
  //     name: cookies.get("username"),
  //     firstName: cookies.get("firstName"),
  //     lastName: cookies.get("lastName"),
  //     hashedPassword: cookies.get("hashedPassword"),
  //   },
  //   token
  // ).then((user) => {
  //   setIsAuth(true);
  // })
  // }


  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)
  const [choiceOne, setChoiceOne] = useState(null)
  const [choiceTwo, setChoiceTwo] = useState(null)
  const [disabled, setDisabled] = useState(false)

  // shuffle the cards 
  const shuffleCards = () => {
    const shuffledCards = [...cardImages, ...cardImages]
      .sort(() => Math.random() - 0.5)
      .map((card) => ({...card, id: Math.random()}))

    setChoiceOne(null)
    setChoiceTwo(null)
    setCards(shuffledCards)
    setTurns(0)
  }

// handle a choice
const handleChoice = (card) => {
  console.log(choiceOne?.id)
  if (card.id === choiceOne?.id) return;
  choiceOne ? setChoiceTwo(card) : setChoiceOne(card)

}

//compare the two selected cards
useEffect (() => {

  if (choiceOne && choiceTwo) {
      setDisabled(true)
    if (choiceOne.src === choiceTwo.src) {
      setCards(prevCards => {
        return prevCards.map(card => {
          if (card.src === choiceOne.src) {
            return {...card, matched:true}
          } else {
            return card
          }
        })
      })
      resetTurn()
    } else{

      setTimeout(() => resetTurn(), 1000)
    }
  }

}, [choiceOne, choiceTwo])

console.log(cards)

useEffect (() => {
  shuffleCards()
}, [])

const resetTurn = () => {
  setChoiceOne(null)
  setChoiceTwo(null)
  setTurns(prevTurns => prevTurns + 1)
  setDisabled(false)
}

  return (
    <div className="app">
     <h1>Memory Game</h1>
      <button onClick={shuffleCards}>New Game</button>

      <div className='card-grid'>
        {cards.map(card => (
        <SingleCard 
        key={card.id} 
        card={card}
        handleChoice={handleChoice}
        flipped = {card === choiceOne || card === choiceTwo || card.matched}
        disabled = {disabled}
        />
        ))}

      </div>
      <p>Turns:{turns}</p>
      {/* {isAuth ? (
        <Chat client={client}>
        <JoinGame   />
      <button onClick={logOut}>Log out</button> 
      </Chat>

      ) : ( 
      <>
      <SignUp setIsAuth={setIsAuth} />
      <Login setIsAuth={setIsAuth} />
      </>
      )} */}
    </div>
  );
}

export default App;
